import axios from "axios";
import authHeader from "./auth-header";

const API_URL = process.env.REACT_APP_API_URL;

class brandService {
  BrandAdd(brand_name,brand_img) {
    const insertData = new FormData();
    insertData.set('brand_name', brand_name);
    insertData.set('brand_img', brand_img[0]);

    return axios
      .post(API_URL + "admin/brand/add", insertData )
      .then((response) => {
        return response.data;
      });
  }

  BrandList() {
    return axios
      .get(API_URL + "admin/brand/list")
      .then((response) => {
        return response.data;
      });
  }

  BrandInfo(brand_id) {
    return axios
      .post(API_URL + "admin/brand/getinfo", { brand_id })
      .then((response) => {
        return response.data;
      });
  }

  BrandUpdate(brand_id, brand_name,brand_img) {
    const updateData = new FormData();
    updateData.set('brand_id', brand_id);
    updateData.set('brand_name', brand_name);
    updateData.set('brand_img', brand_img[0]);

    return axios
      .post(API_URL + "admin/brand/update", updateData )
      .then((response) => {
        return response.data;
      });
  }
  
  BrandDelete(brand_id) {
    return axios
      .post(API_URL + "admin/brand/delete", { brand_id })
      .then((response) => {
        return response.data;
      });
  }
}

export default new brandService();
