  handleSubmit=(e)=>{
    e.preventDefault();

    this.setState({
      loading: true,
    });

    this.Addform.validateAll();

    const { dispatch, history } = this.props;

     var add_product_img = [];
    $('.add_product_img').each(function(index, element){
      console.log("element: ")
      console.log(element.files[0])
      add_product_img.push(element.files[0]);
    });

     var add_video = [];
    $('.add_video').each(function(index, element){
      console.log("element: ")
      console.log(element.files[0])
      add_video.push(element.files[0]);
    });

    if (this.checkBtn.context._errors.length === 0) {
      dispatch(ProductAdd(this.state.title,this.state.name,this.state.description,this.state.keywords,this.state.category_id,this.state.sub_category_id,this.state.start_date_time,this.state.end_date_time,this.state.auction_type,this.state.starting_price,this.state.high_price,this.state.final_price,this.state.refund,this.state.refund_days,this.state.youtube_link,add_product_img,add_video,this.state.start_time,this.state.end_time))
        .then((response) => {
            if(response.success || response.success ==="true" || response.success ===true){
              toast.success(response.message, {position: "bottom-right", autoClose: 5000, hideProgressBar: false, closeonClick: true, pauseOnHover: true, draggable: true, progress: undefined, });
              this.ListProductFun();
              this.setState({ category_id: '',sub_category_id: '',  product_name: '' });
              $("#add_form").modal("hide");
            }else{
              toast.error(response.message, {position: "bottom-right", autoClose: 5000, hideProgressBar: false, closeonClick: true, pauseOnHover: true, draggable: true, progress: undefined, });
            }
        })
        .catch((error) => {
          this.setState({
            loading: false
          });
