          <h1>{this.props.text}</h1>
        <button onClick={this.props.closePopup}>close me</button>
        </div>
      </div>
    );
  }
}
function mapStateToProps(state) {

    const { user } = state.auth;
    const { isLoggedIn } = state.auth;
    const { message } = state.message;
