import React, { Component } from "react";
import { Redirect, Router, Switch, Route, Link, NavLink } from "react-router-dom";

import Form from "react-validation/build/form";
import CheckButton from "react-validation/build/button";

import { connect } from "react-redux";

import { clearMessage } from "./../../actions/message";

import { history } from './../../helpers/history';

import 'bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.js';
import Carousel from 'react-bootstrap/Carousel';

import $ from 'jquery';
import jQuery from 'jquery';
import Popper from 'popper.js';

import './../../assets/css/bootstrap.min.css'

import "./../../assets/css/font-awesome.min.css";
import "./../../assets/website/css/styles.css"

//import "./../../assets/css/style.css"
//import "./../../assets/css/custom.css"
//import "./../../App.css";


import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";


import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import { AdminLogin } from "./../../actions/auth";

import { CategoryList, ProductList, BannerList } from "./../../actions/website/Home";


import Header from './Header';
import Footer from './Footer';
import homebanner from './../../assets/website/img/homebanner.jpg'
import product1 from './../../assets/website/img/product2.png'
// import "react-multi-carousel/lib/styles.css";
import   './../../assets/website/css/sliderstyle.css'
import Slider from "react-slick";
// const Slider = require('react-slick');
// import Slider from "react-slick";

toast.configure();

  


class Createads extends Component {
    constructor(props) {
        super(props);
        // this.handleLoginSubmit = this.handleLoginSubmit.bind(this)
        // this.onChangeEmailID = this.onChangeEmailID.bind(this);
        // this.onChangePassword = this.onChangePassword.bind(this);
        // this.onChangeMobile=this.onChangeMobile.bind(this);
        // this.state = { showPopup: false ,email_id:"",
        // password:"", mobile_no:""};
    
        history.listen((location) => {
          props.dispatch(clearMessage()); // clear message when changing location
        });
      }
    
      componentDidMount() {
       
        var lang
        lang = localStorage.getItem("userId");
        console.log("value of lang is")
        console.log(lang)
        if(lang!=null){
          console.log(lang)
          this.setState({
            showPopup: true
          });
        }
    
      }

      render() {

        const { isLoggedIn, message } = this.props;
    
        
        return (
          <React.Fragment>
              <div id="main-wrapper">

<Header />
<div className="main-banner" id="top">
<img  
src={homebanner}  />
</div>
              	<div class="bottom-header">
	<div class="container">
		<div class="row">
		
				
			</div>
		</div>
	</div>
    <section class="breadcrumb-products my-bid">
	<div class="container">
		<div class="row">
			<div class="col-md-12">

				<nav aria-label="breadcrumb">
				  <ol class="breadcrumb">
					<li class="breadcrumb-item"><a href="#">Home</a></li>
					<li class="breadcrumb-item"><a href="#">Add Products</a></li>
				  </ol>
				</nav>
            </div>
		</div>
	</div>
</section>
<section class="our-products-details">
	<div class="container">
		<div class="row">
			<div class="col-md-6">
			<div class="product-slider">

				<div id='carousel-custom' class='carousel slide' data-ride='carousel'>
					<div class='carousel-outer'>
						
						<div class='carousel-inner '>
							<div class='item active'>
								<img src={product1} alt=''id="zoom_05"  data-zoom-image="images/product2.png"/>
							</div>
							


						</div>
							
						
						<a class='left carousel-control' href='#carousel-custom' data-slide='prev'>
							<span class='glyphicon glyphicon-chevron-left'></span>
						</a>
						<a class='right carousel-control' href='#carousel-custom' data-slide='next'>
							<span class='glyphicon glyphicon-chevron-right'></span>
						</a>
					</div>
					
					
					<ol class='carousel-indicators mCustomScrollbar meartlab'>
						<li data-target='#carousel-custom' data-slide-to='0' class='active'><img src='images/product2.png' alt='' /></li>
						<li data-target='#carousel-custom' data-slide-to='1'><img src='images/product2.png' alt='' /></li>
						<li data-target='#carousel-custom' data-slide-to='2'><img src='images/product2.png' alt='' /></li>
						<li data-target='#carousel-custom' data-slide-to='3'><img src='images/product2.png' alt='' /></li>
						<li data-target='#carousel-custom' data-slide-to='4'><img src='images/product2.png' alt='' /></li>

					</ol>
				</div>
				</div>
			</div>
			
			<div class="col-md-6">
				<div class="add-my-product">	
					<h1>Add Products</h1>
					<div class="products-form">
						<form>
							<div class="col-md-12">
								<div class="form-group">
									<input type="text" name="title" placeholder="Tilte"/>
								</div>
							</div>
							
							<div class="col-md-12">
								<div class="form-group">
									<input type="text" name="name" placeholder="Name"/>
								</div>
							</div>							
							
							<div class="col-md-6">
								<div class="form-group">
									<select class="select-countr">
										<option>Select Country</option>
										<option selected="">Select Country</option>
										<option>Select Country</option>
										<option>Select Country</option>
									</select>
								</div>
							</div>							
							
							<div class="col-md-6">
								<div class="form-group">
									<select class="select-city">
										<option>Select City</option>
										<option selected>India</option>
										<option>India</option>
										<option>India</option>
									</select>
								</div>
							</div>								
							
							<div class="col-md-6">
								<div class="form-group">
									<select class="select-Category">
										<option>Select Category </option>
										<option selected>Category</option>
										<option>Category</option>
										<option>Category</option>
									</select>
								</div>
							</div>							
							
							<div class="col-md-6">
								<div class="form-group">
									<select class="select-Brand">
										<option>Select Brand </option>
										<option selected>Brand</option>
										<option>Brand</option>
										<option>Brand</option>
									</select>
								</div>
							</div>								
							
							<div class="col-md-6">
								<div class="form-group">
									<select class="select-Model">
										<option>Select Model </option>
										<option selected>Model</option>
										<option>Model</option>
										<option>Model</option>
									</select>
								</div>
							</div>	
							
							<div class="col-md-6">
								<div class="form-group">
									<select class="select-Auction">
										<option>Select Auction type</option>
										<option selected>Auction</option>
										<option>Auction</option>
										<option>Auction</option>
									</select>
								</div>
							</div>								
							
							<div class="col-md-6">
								<div class="form-group">
									<input type="text" name="price" placeholder="Price"/>
								</div>
							</div>								
							
							
							<div class="col-md-6">
								<div class="form-group">
									<input type="text" name="Keywords" placeholder="Keywords"/>
								</div>
							</div>								
														
							{/* <div class="col-md-6">
								<div class="form-group">
									<select class="select-Refund ">
										<option>Refund </option>
										<option selected>Refund </option>
										<option>Refund </option>
										<option>Refund </option>
									</select>
								</div>
							</div>	 */}
														
							
							{/* <div class="col-md-6">
								<div class="form-group">
									<input type="text" name="Refund" placeholder="Refund Days"/>
								</div>
							</div>							 */}
							
								
							<div class="col-md-12">
								<div class="form-group">
									<textarea name="your-message"  placeholder="Description"></textarea>
								</div>
							</div>		

							<div class="col-md-12">
								<div class="post-btn form-group">
									<input type="submit" value="Post" class="post-submit"/>
								</div>
							</div>								
							
						
							
						</form>
					</div>
				</div>
            </div>	


			
			
		</div>
	</div>
</section>
</div>
</React.Fragment>
    );
    }

}
    function mapStateToProps(state) {

        const { user } = state.auth;
        const { isLoggedIn } = state.auth;
        const { message } = state.message;
        return {
            user,
            isLoggedIn,
            message
        };
    }
    
    export default connect(mapStateToProps)(Createads);
