import React, { Component } from "react";
import { Redirect, Router, Switch, Route, Link, NavLink } from "react-router-dom";

import Form from "react-validation/build/form";
import CheckButton from "react-validation/build/button";

import { connect } from "react-redux";

import { clearMessage } from "./../../actions/message";

import { history } from './../../helpers/history';

import 'bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.js';
import Carousel from 'react-bootstrap/Carousel';

import $ from 'jquery';
import jQuery from 'jquery';
import Popper from 'popper.js';

import './../../assets/css/bootstrap.min.css'

import "./../../assets/css/font-awesome.min.css";
import "./../../assets/website/css/styles.css"

//import "./../../assets/css/style.css"
//import "./../../assets/css/custom.css"
//import "./../../App.css";


import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";


import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import { AdminLogin } from "./../../actions/auth";

import { CategoryList, ProductList, BannerList } from "./../../actions/website/Home";


import Header from './Header';
import Footer from './Footer';
import homebanner from './../../assets/website/img/homebanner.jpg'

// import "react-multi-carousel/lib/styles.css";
import   './../../assets/website/css/sliderstyle.css'
import Slider from "react-slick";
// const Slider = require('react-slick');
// import Slider from "react-slick";

toast.configure();

  


class Createads extends Component {
    constructor(props) {
        super(props);
        // this.handleLoginSubmit = this.handleLoginSubmit.bind(this)
        // this.onChangeEmailID = this.onChangeEmailID.bind(this);
        // this.onChangePassword = this.onChangePassword.bind(this);
        // this.onChangeMobile=this.onChangeMobile.bind(this);
        // this.state = { showPopup: false ,email_id:"",
        // password:"", mobile_no:""};
    
        history.listen((location) => {
          props.dispatch(clearMessage()); // clear message when changing location
        });
      }
    
      componentDidMount() {
       
        var lang
        lang = localStorage.getItem("userId");
        console.log("value of lang is")
        console.log(lang)
        if(lang!=null){
          console.log(lang)
          this.setState({
            showPopup: true
          });
        }
    
      }

      render() {

        const { isLoggedIn, message } = this.props;
    
        
        return (
          <React.Fragment>
              <div id="main-wrapper">

<Header />
<div className="main-banner" id="top">
<img  
src={homebanner}  />
</div>
              	<div class="bottom-header">
	<div class="container">
		<div class="row">
			<div class="col-md-3">
		
				<div class="logo">
					<a href="index.html"><img src="images/logo.png"/></a>
				</div>
			</div>
				<div class="menulist-item" id="mySidenav">	
							
								
					</div>	
			</div>
		</div>
	</div>
    <section class="breadcrumb-products my-bid">
	<div class="container">
		<div class="row">
			<div class="col-md-12">

				<nav aria-label="breadcrumb">
				  <ol class="breadcrumb">
					<li class="breadcrumb-item"><a href="#">Home</a></li>
					<li class="breadcrumb-item"><a href="#">Add Products</a></li>
				  </ol>
				</nav>
            </div>
		</div>
	</div>
</section>
</div>
</React.Fragment>
    );
    }

}
    function mapStateToProps(state) {

        const { user } = state.auth;
        const { isLoggedIn } = state.auth;
        const { message } = state.message;
        return {
            user,
            isLoggedIn,
            message
        };
    }
    
    export default connect(mapStateToProps)(Createads);
